import logo from './logo.svg';
import './App.css';
import {useState} from "react";

function App() {

    let [inputMsg, setInputMsg] = useState('')
    let [prompt, setPrompt] = useState('아직 시작전')

    function predictNextSentence(text, temp, items) {
        return fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
                'temp': temp
            })
        })
            .then((response) => response.json())
            .then((data) => setPrompt(data));
    }

    function userInput(text, items) {
        return fetch('http://0.0.0.0:8000/user', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
            })
        })
            .then((response) => response.json())
            .then((data) => setPrompt(data));
    }

    function isErroneous(texts) {
        return (texts.includes('1') || texts.includes('0'))
    }

    function concatTwo(texts) {
        if (texts.split(". ").length >= 2) {
            return texts.split(". ")[0] + ". " + texts.split(". ")[1] + ". ";
        } else {
            return texts;
        }
    }


    return (
        <div className="App">
            <div>
                <h4>{prompt}</h4>
                <input onChange={(e) => {
                    setInputMsg(e.target.value)
                }}></input>
                <button onClick={() => {
                    userInput({inputMsg}, 0.5)
                    console.log(inputMsg)
                }}>제발 되면 좋겠다.
                </button>
            </div>
        </div>
    );
}

export default App;
