import {useEffect, useState, React} from "react";
import {
    addDoc,
    doc,
    getDoc,
    setDoc,
    collection,
    serverTimestamp,
    onSnapshot,
    query,
    where,
    orderBy,
    getCountFromServer,
} from 'firebase/firestore'
import {auth, db} from "../firebase-config";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from "react-bootstrap/Card";
import {ScaleLoader, BeatLoader} from "react-spinners";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {ko} from 'date-fns/esm/locale';

function Writing(props) {
    //first, we need to set the date of the journal and create the doc for that specific date
    //다이어리 세션 시작을 위한 문서 작성
    let [sessionStatus, setSessionStatus] = useState(false)
    let [loading, setLoading] = useState(true)


    let [newDiary, setNewDiary] = useState("")
    let [date, setDate] = useState(new Date());
    let [showConversation, setShowConversation] = useState(false)
    let [refresh, setRefresh] = useState(1)
    let [inputUser, setInputUser] = useState('')
    let [inConversation, setInConversation] = useState('')
    let [promptForLLM, setPromptForLLM] = useState('')
    const sessionRef = collection(db, "session")
    const promptsRef = collection(db, "prompt")
    const [tempText, setTemp] = useState('')

    async function createNewDoc() {
        const coll = collection(db, "session", props.userName, "diary")
        const existingSession = await getCountFromServer(coll)
        const sessionNum = (existingSession.data().count + 1)
        await setDoc(doc(db, "session", props.userName, "diary", String(sessionNum)), {
            contents: [{
                message: '',
                role: ''
            }],
            lastMessage: "",
            user: auth.currentUser.displayName,
            isFinish: false
        });
        setSessionStatus(true)
        setLoading(true)
        setRefresh(refresh + 1)
    }

    useEffect(() => {
        async function fetchPrompt() {
            if (sessionStatus) {
                setLoading(true);
                try {
                    const promptResult = await assemblePrompt();
                    setPromptForLLM(promptResult);
                    await requestPrompt(promptResult, 0.5, "");
                    // any other code that needs to run before console.log(tempText)
                } catch (error) {
                    // handle any errors that might occur
                } finally {
                }
            }
        }
        fetchPrompt();
    }, [refresh]);

    useEffect(() => {
         getLastSentence(String(tempText))
    }, [tempText])

    async function assemblePrompt() {
        let text = ''
        let instruction = ''
        let sentences = []
        let sentences_2 = []
        const docRef = doc(db, 'prompt', 'module1_1');
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
            sentences = docSnap.data().demonstration;
            // console.log(sentences)
            sentences_2 = shuffleArray(sentences)
            // console.log(sentences_2)
            instruction = docSnap.data().instruction;
            // console.log(text)
        } else {
            // doc.data() will be undefined in this case
            console.log("No such document!");
        }
        let temp = ""
        let i = 0
        while (true) {
            temp = temp + sentences_2[i]
            if (sentences_2.length == i) {
                // console.log(text)
                return text
            }
            text = instruction + temp + inConversation
            if (text.length >= 4000) {
                // console.log(text)
                return text
            }
            i++
        }
    }

    function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array
    }

    async function requestPrompt(text, temp, event, items) {
        return await fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
                'temp': temp,
                'event': event,
            })
        })
            .then((response) => response.json())
            .then((data) => {
                setTemp(data)
                // let a = setTimeout(() => {
                //     setInitiation(false)
                // }, 3000)
            })
    }

    async function getLastSentence(response) {
        // console.log(response)
        let loc_1 = response.lastIndexOf("A: ");
        let temp_text = response.slice(loc_1 + 3);
        // console.log(temp_text)
        let loc_2 = temp_text.indexOf("\n");
        let output = temp_text.substring(0, loc_2 - 4)
        let addconversation = inConversation + 'A: ' + output + '\n'
        // setInConversation(addconversation)
        props.setPrompt(output)
        console.log(output)
        // setLoading(false)
    }


    function setOpenConversation() {
        if (showConversation) {
            setShowConversation(false)
            // console.log('나 여깄어~')
        } else {
            setShowConversation(true)
        }
    }


    function addConversationFromUser(input) {
        let temp = inConversation + 'B: ' + input + '\n'
        setInConversation(temp)
        setRefresh(refresh + 1)
    }

    if (sessionStatus == false) {
        return (
            <Container>
                <Row>
                    <div className="loading_box">
                        오늘의 마음챙김 세션을 시작합니다.
                    </div>
                </Row>
                <Row>
                    <Col>
                        <div className="d-grid gap-2">
                            <Button
                                variant="primary"
                                style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                onClick={createNewDoc}
                            >
                                오늘의 다이어리 작성하기
                            </Button>
                        </div>
                    </Col>
                    <Col>

                    </Col>
                </Row>
            </Container>

        )
    } else {
        return (
            <Container>
                <Row>
                    <div>
                        {loading === true ? <Loading refresh={refresh}/> :
                            <Userinput prompt={props.prompt} setInputUser={setInputUser} inputUser={inputUser}
                                       addConversationFromUser={addConversationFromUser}/>}
                    </div>


                    <div className="inwriting_review_box">
                        <Container>
                            <Row xs={'auto'} md={1} className="g-4">
                                <Col>
                                    <Card style={{
                                        width: '100%',
                                    }}>
                                        <Card.Body>
                                            <Card.Title> <BeatLoader color="#007AFF" size={10}/> 일기 작성중</Card.Title>
                                            <Card.Subtitle className="mb-2 text-muted">
                                                <div>핵심키워드 도출 중</div>
                                            </Card.Subtitle>
                                            <Card.Text>
                                                오늘은 이기적인 사람들에 대해 생각해보았다. 나는 다른 사람을 배려하지만, 그들은 나를 그렇게 배려해주지는 않는 것 같다.
                                                하지만, 나의
                                                배려속에 상대방이 보답해주기를 바라는 마음이 있는 것은 아닐까? 이것은 '미숙한 착함'이 될 수 있다. 어떻게 하면 더 성숙할 수
                                                있을지
                                                고민해봐야겠다.
                                            </Card.Text>
                                        </Card.Body>
                                    </Card>
                                    <div
                                        style={{
                                            margin: "1em auto 2em"
                                        }}>
                                        <Button
                                            variant="secondary"
                                            size="sm"
                                            style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                            onClick={() => {
                                                setOpenConversation()
                                            }}>
                                            {showConversation === true ? <div>▲ 전체 대화내용 닫기</div> :
                                                <div>▼ 전체 대화내용 보기</div>}
                                        </Button>
                                    </div>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                </Row>
                <Row>
                    <Col>
                    </Col>
                    <Col>
                    </Col>
                </Row>
            </Container>
        )
    }


}

function Loading(props) {
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="loading_box">
                            <div>
                                <ScaleLoader
                                    color="#007AFF"
                                    speedMultiplier={0.9}
                                />
                            </div>
                            <div>{props.refresh === 1 ? <div>곧 일기 작성이 시작됩니다</div> : <div>지금까지의 이야기를 정리중입니다</div>}</div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">✏️ 나의 일기 입력하기</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            disabled
                            readOnly
                        />
                        <Form.Text id="userInput" muted>
                            📝 정해진 양식은 없어요. 편안하고 자유롭게 최근에 있었던 일을 작성해주세요.
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        disabled={true}
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                    >응답 기록하기</Button>
                                </div>
                            </Col>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="secondary"
                                        disabled={true}
                                        style={{backgroundColor: "264362", fontWeight: "600"}}
                                    >일기 종료하기</Button>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

//User input screen component
function Userinput(props) {
    //for textfield monitoring
    let [temp_input, setTemp_input] = useState('')
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="prompt_box">
                            <div className="tte">
                                {props.prompt}
                            </div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">✏️ 나의 일기 입력하기</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            onChange={(e) => {
                                setTemp_input(e.target.value)
                            }}
                        />
                        <Form.Text id="userInput" muted>
                            📝 정해진 양식은 없어요. 편안하고 자유롭게 최근에 있었던 일을 작성해주세요.
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                        onClick={() => {
                                            (function () {
                                                if (temp_input.length < 11) {
                                                    alert('입력한 내용이 너무 짧아요. 조금 더 길게 적어볼까요?')
                                                } else {
                                                    props.addConversationFromUser(temp_input)
                                                }
                                            })()
                                        }}>응답 기록하기</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

export default Writing
