import {useEffect, useState, useRef, React} from "react";
import {
    addDoc,
    doc,
    getDoc,
    setDoc,
    collection,
    serverTimestamp,
    onSnapshot,
    query,
    where,
    orderBy,
    getCountFromServer,
} from 'firebase/firestore'
import {auth, db} from "../firebase-config";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from "react-bootstrap/Card";
import {ScaleLoader, BeatLoader} from "react-spinners";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {ko} from 'date-fns/esm/locale';

function Writing(props) {

    //ÌôîÎ©¥Í≥º Í∏∞Îä• Ï†ÑÌôò Ï†ïÏùòÎ•º ÏúÑÌïú state
    let [sessionStatus, setSessionStatus] = useState(false) //ÏÑ∏ÏÖò ÏãúÏûë ÌôïÏù∏
    let [loading, setLoading] = useState(true) //Î°úÎî©(request API) ÌôïÏù∏
    const [tempText, setTemp] = useState('') //get-last-sentence ÌôïÏù∏

    //ÌòÑÏû¨ ÏÑ∏ÏÖò Ï†ïÎ≥¥
    let [currentSession, setCurrentSession] = useState("")//ÏÑ∏ÏÖò ÎÑòÎ≤Ñ
    let [inConversation, setInConversation] = useState('')//Î¥á,ÏÇ¨Ïö©Ïûê ÎåÄÌôîÍ∏∞Î°ù
    let [inputUser, setInputUser] = useState('')//ÏÇ¨Ïö©ÏûêÍ∞Ä textinputÏóê ÏûÖÎ†•Ìïú ÎÇ¥Ïö©
    let [promptForLLM, setPromptForLLM] = useState('') //LLMÏöîÏ≤≠ÏùÑ ÏúÑÌïú promptset

    //Í∏∞ÌÉÄ
    let [showConversation, setShowConversation] = useState(false) //ÎåÄÌôî ÏõêÎ≥∏ Î≥¥Ïó¨Ï£ºÍ∏∞/Í∞ÄÎ¶¨Í∏∞

    async function createNewDoc() {
        const coll = collection(db, "session", props.userName, "diary")
        const existingSession = await getCountFromServer(coll)
        const sessionNum = await (existingSession.data().count + 1)
        setCurrentSession(sessionNum)
        await setDoc(doc(db, "session", props.userName, "diary", String(sessionNum)), {
            lastMessage: "",
            user: auth.currentUser.displayName,
            isFinish: false
        });
        setSessionStatus(true)
        setLoading(true)
    }

    useEffect(() => {
        async function fetchPrompt() {
                if (loading) {
                    try {
                        const promptSet = await assemblePrompt();
                        setPromptForLLM(promptSet);
                        await requestPrompt(promptForLLM, 0.5, "");
                        // any other code that needs to run before console.log(tempText)
                    } catch (error) {
                        // handle any errors that might occur
                    } finally {
                        console.log(promptForLLM)

                    }
                } else {
                    console.log("fetchpromptÍ±∏Î¶º")
                }
        }
        fetchPrompt()
    }, [loading])

    async function assemblePrompt() {
        let text = ''
        let instruction = ''
        let sentences = []
        let sentences_2 = []
        const docRef = doc(db, 'prompt', 'module1_1');
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
            sentences = docSnap.data().demonstration;
            // console.log(sentences)
            sentences_2 = shuffleArray(sentences)
            // console.log(sentences_2)
            instruction = docSnap.data().instruction;
            // console.log(text)
        } else {
            // doc.data() will be undefined in this case
            console.log("No such document!");
        }
        let temp = ""
        let i = 0
        while (true) {
            temp = temp + sentences_2[i]
            if (sentences_2.length == i) {
                // console.log(text)
                return text
            }
            text = instruction + temp + inConversation
            if ((text + sentences_2[i + 1]).length >= 4000) {
                // console.log(text)
                return text
            }
            i++
        }

    }

    async function requestPrompt(text, temp, event, items) {
        // console.log(text)
        return await fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
                'temp': temp,
                'event': event,
            })
        })
            .then((response) => response.json())
            .then((data) => {
                setTemp(data)
                // let a = setTimeout(() => {
                //     setInitiation(false)
                // }, 3000)
            })
    }


    useEffect(() => {
        if (tempText !== "") {
            getLastSentence(String(tempText))
            let a = setTimeout(() => {
                setLoading(false)
            }, 3000)
            return () => {
                clearTimeout(a)
            }
        } else {
            console.log("tempText is empty")
        }

    }, [tempText])

    async function getLastSentence(response) {
        // console.log(response)
        let loc_1 = response.lastIndexOf("A: ");
        let temp_text = response.slice(loc_1 + 3);
        // console.log(temp_text)
        let loc_2 = temp_text.indexOf("\n");
        let output = temp_text.substring(0, loc_2 - 4)
        // let addconversation = inConversation + 'A: ' + output + '\n'
        // setInConversation(addconversation)
        props.setPrompt(output)
        // console.log(output)
        // setLoading(false)
    }

    function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array
    }


    function setOpenConversation() {
        if (showConversation) {
            setShowConversation(false)
            // console.log('ÎÇò Ïó¨ÍπÑÏñ¥~')
        } else {
            setShowConversation(true)
        }
    }


    /*async function addConversationFromUser(input) {
        let temp = "A: " + props.prompt + "\n" + 'B: ' + input + '\n';
        const docRef2 = doc(db, "session", props.userName, "diary", "1");
        const docSnap2 = await getDoc(docRef2);
        if (docSnap2.exists()) {
            const message = (docSnap2.data().lastMessage)
            let temptxt = message + temp
            setInConversation(temptxt)
        } else {
            console.log("Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå")
        }
        await setDoc(docRef2, {
                lastMessage: inConversation
            });
        setLoading(true)
    }*/

    async function addConversationFromUser(input) {
        let temp = "\\n" + "A: " + props.prompt + "\\n" + 'B: ' + input + "\\n";
        const docRef2 = doc(db, "session", props.userName, "diary", "test");
        const docSnap2 = await getDoc(docRef2);
        if (docSnap2.exists()) {
            const message = docSnap2.data().lastMessage;
            let temptxt = message + temp;
            setInConversation(temptxt);
            await setDoc(docRef2, {
                lastMessage: temptxt
            });
        } else {
            console.log("Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå");
        }
        setLoading(true);
    }

    if (sessionStatus == false) {
        return (
            <Container>
                <Row>
                    <div className="loading_box">
                        Ïò§ÎäòÏùò ÎßàÏùåÏ±ôÍπÄ ÏÑ∏ÏÖòÏùÑ ÏãúÏûëÌï©ÎãàÎã§.
                    </div>
                </Row>
                <Row>
                    <Col>
                        <div className="d-grid gap-2">
                            <Button
                                variant="primary"
                                style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                onClick={createNewDoc}
                            >
                                Ïò§ÎäòÏùò Îã§Ïù¥Ïñ¥Î¶¨ ÏûëÏÑ±ÌïòÍ∏∞
                            </Button>
                        </div>
                    </Col>
                    <Col>

                    </Col>
                </Row>
            </Container>

        )
    } else {
        return (
            <Container>
                <Row>
                    <div>
                        {loading === true ? <Loading/> :
                            <Userinput prompt={props.prompt} setInputUser={setInputUser} inputUser={inputUser}
                                       addConversationFromUser={addConversationFromUser}/>}
                    </div>


                    <div className="inwriting_review_box">
                        <Container>
                            <Row xs={'auto'} md={1} className="g-4">
                                <Col>
                                    <Card style={{
                                        width: '100%',
                                    }}>
                                        <Card.Body>
                                            <Card.Title> <BeatLoader color="#007AFF" size={10}/> ÏùºÍ∏∞ ÏûëÏÑ±Ï§ë</Card.Title>
                                            <Card.Subtitle className="mb-2 text-muted">
                                                <div>ÌïµÏã¨ÌÇ§ÏõåÎìú ÎèÑÏ∂ú Ï§ë</div>
                                            </Card.Subtitle>
                                            <Card.Text>
                                                Ïò§ÎäòÏùÄ Ïù¥Í∏∞Ï†ÅÏù∏ ÏÇ¨ÎûåÎì§Ïóê ÎåÄÌï¥ ÏÉùÍ∞ÅÌï¥Î≥¥ÏïòÎã§. ÎÇòÎäî Îã§Î•∏ ÏÇ¨ÎûåÏùÑ Î∞∞Î†§ÌïòÏßÄÎßå, Í∑∏Îì§ÏùÄ ÎÇòÎ•º Í∑∏Î†áÍ≤å Î∞∞Î†§Ìï¥Ï£ºÏßÄÎäî ÏïäÎäî Í≤É Í∞ôÎã§.
                                                ÌïòÏßÄÎßå, ÎÇòÏùò
                                                Î∞∞Î†§ÏÜçÏóê ÏÉÅÎåÄÎ∞©Ïù¥ Î≥¥ÎãµÌï¥Ï£ºÍ∏∞Î•º Î∞îÎùºÎäî ÎßàÏùåÏù¥ ÏûàÎäî Í≤ÉÏùÄ ÏïÑÎãêÍπå? Ïù¥Í≤ÉÏùÄ 'ÎØ∏ÏàôÌïú Ï∞©Ìï®'Ïù¥ Îê† Ïàò ÏûàÎã§. Ïñ¥ÎñªÍ≤å ÌïòÎ©¥ Îçî ÏÑ±ÏàôÌï† Ïàò
                                                ÏûàÏùÑÏßÄ
                                                Í≥†ÎØºÌï¥Î¥êÏïºÍ≤†Îã§.
                                            </Card.Text>
                                        </Card.Body>
                                    </Card>
                                    <div
                                        style={{
                                            margin: "1em auto 2em"
                                        }}>
                                        <Button
                                            variant="secondary"
                                            size="sm"
                                            style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                            onClick={() => {
                                                setOpenConversation()
                                            }}>
                                            {showConversation === true ? <div>‚ñ≤ Ï†ÑÏ≤¥ ÎåÄÌôîÎÇ¥Ïö© Îã´Í∏∞</div> :
                                                <div>‚ñº Ï†ÑÏ≤¥ ÎåÄÌôîÎÇ¥Ïö© Î≥¥Í∏∞</div>}
                                        </Button>
                                    </div>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                </Row>
                <Row>
                    <Col>
                    </Col>
                    <Col>
                    </Col>
                </Row>
            </Container>
        )
    }


}

function Loading(props) {
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="loading_box">
                            <div>
                                <ScaleLoader
                                    color="#007AFF"
                                    speedMultiplier={0.9}
                                />
                            </div>
                            <div>ÏßÄÍ∏àÍπåÏßÄÏùò Ïù¥ÏïºÍ∏∞Î•º Ï†ïÎ¶¨Ï§ëÏûÖÎãàÎã§</div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">‚úèÔ∏è ÎÇòÏùò ÏùºÍ∏∞ ÏûÖÎ†•ÌïòÍ∏∞</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            disabled
                            readOnly
                        />
                        <Form.Text id="userInput" muted>
                            üìù Ï†ïÌï¥ÏßÑ ÏñëÏãùÏùÄ ÏóÜÏñ¥Ïöî. Ìé∏ÏïàÌïòÍ≥† ÏûêÏú†Î°≠Í≤å ÏµúÍ∑ºÏóê ÏûàÏóàÎçò ÏùºÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî.
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        disabled={true}
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                    >ÏùëÎãµ Í∏∞Î°ùÌïòÍ∏∞</Button>
                                </div>
                            </Col>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="secondary"
                                        disabled={true}
                                        style={{backgroundColor: "264362", fontWeight: "600"}}
                                    >ÏùºÍ∏∞ Ï¢ÖÎ£åÌïòÍ∏∞</Button>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

//User input screen component
function Userinput(props) {
    //for textfield monitoring
    let [temp_input, setTemp_input] = useState('')
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="prompt_box">
                            <div className="tte">
                                {props.prompt}
                            </div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">‚úèÔ∏è ÎÇòÏùò ÏùºÍ∏∞ ÏûÖÎ†•ÌïòÍ∏∞</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            onChange={(e) => {
                                setTemp_input(e.target.value)
                            }}
                        />
                        <Form.Text id="userInput" muted>
                            üìù Ï†ïÌï¥ÏßÑ ÏñëÏãùÏùÄ ÏóÜÏñ¥Ïöî. Ìé∏ÏïàÌïòÍ≥† ÏûêÏú†Î°≠Í≤å ÏµúÍ∑ºÏóê ÏûàÏóàÎçò ÏùºÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî.
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                        onClick={() => {
                                            (function () {
                                                if (temp_input.length < 11) {
                                                    alert('ÏûÖÎ†•Ìïú ÎÇ¥Ïö©Ïù¥ ÎÑàÎ¨¥ ÏßßÏïÑÏöî. Ï°∞Í∏à Îçî Í∏∏Í≤å Ï†ÅÏñ¥Î≥ºÍπåÏöî?')
                                                } else {
                                                    props.addConversationFromUser(temp_input)
                                                }
                                            })()
                                        }}>ÏùëÎãµ Í∏∞Î°ùÌïòÍ∏∞</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

export default Writing
