import {useEffect, useState} from "react";

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import React from 'react';

import {ScaleLoader} from "react-spinners";


function Writing(props) {
    //Ï≤òÏùåÏù∏ÏßÄ ÏïÑÎãåÏßÄ ÌôïÏù∏ÌïòÎäî Î≥ÄÏàò
    let [initiation, setInitiation] = useState(true)
    let [refresh, setRefresh] = useState(1)
    //Î°úÏª¨ ÎåÄÌôîÎÇ¥Ïö©
    let [inConversation, setInConversation] = useState('')
    //ÎåÄÌôî ÏûÖÎ†• ÏóÖÎç∞Ïù¥Ìä∏
    let [inputUser, setInputUser] = useState('')

    useEffect(() => {
        //Ïó¨Í∏∞Ï†ÅÏùÄ ÏΩîÎìúÎäî Ïª¥Ìè¨ÎÑåÌä∏ Î°úÎìú & ÏóÖÎç∞Ïù¥Ìä∏ ÎßàÎã§ Ïã§ÌñâÎê®
        //console.log('ÏïàÎÖï')
        setInitiation(true)
        predictNextSentence(inConversation, 0.5)
        //console.log(props.prompt)

    }, [refresh]);

    // useEffect(() => {
    //     let temp = inConversation + 'B: ' + inputUser + '\n'
    //     setInConversation(temp)
    //     setRefresh(refresh + 1)
    // }, [inputUser]);

    //llmÏöîÏ≤≠ Ìï®Ïàò
    function predictNextSentence(text, temp, items) {
        return fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
                'temp': temp,
            })
        })
            .then((response) => response.json())
            .then((data) => {
                let prompt = getLastSentence(data)
                //promptÎßå Ï∂îÏ∂úÌï¥ÏÑú Ï†ÄÏû•Ìï¥ÏïºÌï®
                props.setPrompt(prompt)
                let a = setTimeout(() => {
                    setInitiation(false)
                    //console.log(inConversation)
                }, 1000);

            });
    }

    function getLastSentence(response) {
        let loc_1 = response.lastIndexOf("A: ");
        let temp_text = response.slice(loc_1 + 3);
        let loc_2 = temp_text.indexOf("\n") - 1;
        let output = temp_text.substring(0, loc_2)
        let addconversation = inConversation + 'A: ' + output + '\n'
        setInConversation(addconversation)
        return output
    }



    return (
        <div>
            <div>{initiation === true ? <Initiation/> : <Userinput prompt={props.prompt} inputUser={inputUser} setInputUser={setInputUser()} />}</div>
        </div>
    )
}

function Initiation() {
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="loading_box">
                            <div>
                                <ScaleLoader
                                    color="#007AFF"
                                    speedMultiplier={0.9}
                                />
                            </div>
                            <div>Í≥ß ÏùºÍ∏∞ ÏûëÏÑ±Ïù¥ ÏãúÏûëÎê©ÎãàÎã§</div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">‚úèÔ∏è ÎÇòÏùò ÏùºÍ∏∞ ÏûÖÎ†•ÌïòÍ∏∞</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            disabled
                            readOnly
                        />
                        <Form.Text id="userInput" muted>
                            üìù Ï†ïÌï¥ÏßÑ ÏñëÏãùÏù¥ÎÇò ÏùëÎãµÏù¥ ÏóÜÏñ¥Ïöî. Ìé∏ÏïàÌïòÍ≥† ÏûêÏú†Î°≠Í≤å ÏµúÍ∑ºÏóê ÏûàÏóàÎçò ÏùºÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî .
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        disabled={true}
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                    >ÏùëÎãµ Í∏∞Î°ùÌïòÍ∏∞</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

function Userinput(props) {

    let [temp_input, setTemp_input] = useState('')


    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="prompt_box">
                            <div className="tte">
                                {props.prompt}
                            </div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">‚úèÔ∏è ÎÇòÏùò ÏùºÍ∏∞ ÏûÖÎ†•ÌïòÍ∏∞</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            onChange={(e) => {
                                //props.setInputMsg(e.target.value)
                                //console.log(e.target.value)
                                setTemp_input(e.target.value)
                            }}
                        />
                        <Form.Text id="userInput" muted>
                            üìù Ï†ïÌï¥ÏßÑ ÏñëÏãùÏù¥ÎÇò ÏùëÎãµÏù¥ ÏóÜÏñ¥Ïöî. Ìé∏ÏïàÌïòÍ≥† ÏûêÏú†Î°≠Í≤å ÏµúÍ∑ºÏóê ÏûàÏóàÎçò ÏùºÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî .
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                        onClick={()=>{
                                            props.setInputUser(temp_input)
                                        }}
                                    >ÏùëÎãµ Í∏∞Î°ùÌïòÍ∏∞</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>

    )
}

export default Writing
