    const sessionStatus = useRef(false)
    const diaryNumber = useRef("");
    const receivedText = useRef("");
    const inConversation = useRef("");
    const promptForLM = useRef("");
    // let [inConversation, setInConversation] = useState("")//ë´‡,ì‚¬ìš©ì ëŒ€í™”ê¸°ë¡
    let [inputUser, setInputUser] = useState('')//ì‚¬ìš©ìê°€ textinputì— ì…ë ¥í•œ ë‚´ìš©
    // let [promptForLLM, setPromptForLLM] = useState('') //LLMìš”ì²­ì„ ìœ„í•œ promptset
    let [prompt, setPrompt] = useState('')

    //ê¸°íƒ€
    let [showConversation, setShowConversation] = useState(false) //ëŒ€í™” ì›ë³¸ ë³´ì—¬ì£¼ê¸°/ê°€ë¦¬ê¸°

    //whisper ê´€ë ¨

    async function createNewDoc() {
        const coll = collection(db, "session", props.userName, "diary")
        const existingSession = await getCountFromServer(coll)
        const sessionNum = await (existingSession.data().count + 1)
        diaryNumber.current = String(sessionNum)
        await setDoc(doc(db, "session", props.userName, "diary", String(sessionNum)), {
            outputFromLM: "ë‚˜: ì˜¤ëŠ˜ ìˆì—ˆë˜ ì¼ì— ëŒ€í•´ ë“£ê³  ì‹¶ì–´ìš”. ì •í•´ì§„ ê·œì¹™ì´ ì—†ìœ¼ë‹ˆ ììœ ë¡­ê²Œ ì–˜ê¸°í•´ì£¼ì„¸ìš”.\\nì‚¬ìš©ì: ì˜¤ëŠ˜ë„ í‰ë²”í•˜ê²Œ ì¶œê·¼í•˜ê³ , ë­ ë³„ì¼ì€ ì—†ì—ˆì–´ìš”.\\në‚˜: ê·¸ë¬êµ°ìš”. ì¡°ê¸ˆ ë” ìì„¸í•œ ì´ì•¼ê¸°ë¥¼ ë“£ê³  ì‹¶ì€ë°. ì˜¤ëŠ˜ í•˜ë£¨ ê°€ì¥ ê¸°ì–µì— ë‚¨ëŠ” í•œ ê°€ì§€ ì¼ì„ ê³¨ë¼ë³¸ë‹¤ë©´ ë¬´ì—‡ì´ ìˆì„ê¹Œìš”?\\nì‚¬ìš©ì: ê¸€ì„. ì¼ë‹¨ ì˜¤ëŠ˜ ì•„ì¹¨ì— ì—…ë¬´ ë¯¸íŒ…ì´ í•˜ë‚˜ ìˆì—ˆê³ , ì €ë…ì—ëŠ” ì˜¤ëœë§Œì— ë¶€ëª¨ë‹˜ê³¼ ì‹ì‚¬í–ˆë„¤ìš”.\\në‚˜: ê·¸ë¬êµ°ìš”. ì–˜ê¸°í•´ì¤˜ì„œ ê³ ë§ˆì›Œìš”! ì˜¤ëŠ˜ ì—…ë¬´ ë¯¸íŒ…ì—ì„œ ë³„ì¼ì€ ì—†ì—ˆë‚˜ìš”?\\nì‚¬ìš©ì: ìŒ í° ë¯¸íŒ…ì€ ì•„ë‹ˆì—ˆëŠ”ë°, ìš”ì¦˜ ê³„ì† ì œ ìƒì‚¬ê°€ ì €ë¥¼ ë¬´ì‹œí•˜ê³ , ì•ˆ ì¢‹ê²Œ ë³´ê³  ìˆëŠ” ê²ƒ ê°™ë‹¤ëŠ” ëŠë‚Œì´ ë“¤ì–´ì„œ í˜ë“¤ì–´ìš”.\\n###\\në‚˜: ì˜¤ëŠ˜ í•˜ë£¨ëŠ” ì–´ë• ë‚˜ìš”? í¸ì•ˆí•˜ê²Œ ì–˜ê¸°í•´ì£¼ì„¸ìš”.\\nì‚¬ìš©ì: ì˜¤ëŠ˜ ë³„ì¼ì€ ì—†ì—ˆë˜ ê±° ê°™ì€ë°. ìŒ ìƒê°ì´ ì˜ ì•ˆ ë‚˜ë„¤.\\në‚˜: ê·¸ë¬êµ°ìš”. ê·¸ë˜ë„ ì¢€ ë” ìì„¸í•œ ì¼ìƒ ì´ì•¼ê¸°ë¥¼ ë“£ê³  ì‹¶ì€ë°, ê°€ì¥ ê¸°ì–µì— ë‚¨ëŠ” ì‚¬ê±´ì´ ë­ê°€ ìˆì„ê¹Œìš”?\\nì‚¬ìš©ì: ì˜¤ëŠ˜ ì˜¤ëœë§Œì— ì˜¤ë˜ëœ ì¹œêµ¬ë‘ ë§Œë‚˜ì„œ ì €ë…ì„ ê°™ì´ ë¨¹ì—ˆì–´.\\në‚˜: ì•„ ê·¸ë¬êµ°ìš”. ë¬´ìŠ¨ ì–˜ê¸°ë¥¼ í–ˆë‚˜ìš”?\\nì‚¬ìš©ì: ê·¸ëƒ¥ ì¸ìƒ ì–˜ê¸° í–ˆë˜ ê²ƒ ê°™ì•„. ì§€ê¸ˆê¹Œì§€ ì‚´ì•„ì˜¨ ì´ì•¼ê¸°, ì´ ì–˜ê¸° ì € ì–˜ê¸°? ì‚¬ì‹¤ ëŒ€í•™êµ ì¡¸ì—…í•˜ê³  ì²˜ìŒ ë³´ëŠ” ê±°ë¼ì„œ.\\në‚˜: ê·¸ë ‡êµ°ìš”. ì¬ë¯¸ìˆì—ˆê² ì–´ìš”. ê·¸ëŸ°ë° ê°‘ìê¸° ì´ëŸ° ëŒ€í™”ë¥¼ í•˜ê²Œ ëœ ì´ìœ ê°€ ë”°ë¡œ ìˆì„ê¹Œìš”?\\nì‚¬ìš©ì: ìš°ì—°íˆ, ì¸ìŠ¤íƒ€ê·¸ë¨ì—ì„œ ì´ ì¹œêµ¬ê°€ ë‚˜ë¥¼ ë³´ê³  íŒ”ë¡œìš° í•œ ê±°ì•¼. ê·¸ë˜ì„œ ìš°ì—°íˆ ì—°ê²°ì´ ë˜ì—ˆê³ , ì–˜ê¸°í•˜ë‹¤ ë³´ë‹ˆ ê·¼ì²˜ì— ì‚´ê³  ìˆê³  ê·¸ë ‡ë”ë¼ê³ . ê·¸ë˜ì„œ ì–¸ì œ ìˆ ì´ë‚˜ í•œì”í•˜ìê³  ë‚´ê°€ ì—°ë½í–ˆì§€.\\n###\\në‚˜: ì˜¤ëŠ˜ í•˜ë£¨ë„ ì˜ ë³´ëƒˆë‚˜ìš”? ì–´ë–¤ ì¼ì´ ìˆì—ˆë‚˜ìš”? ì •ë¦¬ê°€ ì•ˆ ë˜ì–´ë„ ë˜ë‹ˆ í¸ì•ˆí•˜ê²Œ í’€ì–´ì£¼ì„¸ìš”.\\nì‚¬ìš©ì: ìŒ ì˜¤ëŠ˜ ë³„ì¼ ì—†ì—ˆëŠ”ë°. ê·¸ëƒ¥ í‰ì†Œì²˜ëŸ¼ ì¼ì–´ë‚˜ê³ , ë°¥ ë¨¹ê³ , í…”ë ˆë¹„ì „ ë³´ê³  ê·¸ë ‡ì§€.\\në‚˜: ê·¸ë¬êµ°ìš”. ê·¸ë ‡ë‹¤ë©´ ì˜¤ëŠ˜ ì¼ìƒ ì¤‘ì—ì„œ ê°€ì¥ ë‚˜ë¥¼ í˜ë“¤ê²Œ ë˜ëŠ” ê¸°ì˜ê²Œ í–ˆë˜ ê²Œ ë­ê°€ ìˆì„ê¹Œìš”?\\nì‚¬ìš©ì: ìŒ ì˜¤ëŠ˜ ê°™ì€ ë³‘ì‹¤ì— ìˆëŠ” ì‚¬ëŒê³¼ ë§ë‹¤íˆ¼ì´ ìˆì—ˆë„¤.\\në‚˜: ì €ëŸ°. ì–´ì©Œë‹¤ ë‹¤í‰œë‚˜ìš”?\\nì‚¬ìš©ì: ë­ í˜¼ìë§Œ ê·¸ë ‡ê²Œ í˜ë“¤ë‹¤ê³  ì„¸ìƒ í•œíƒ„ì„ í•˜ëŠ”ë°. ê°€ë§Œíˆ ë“£ê³  ìˆìë‹ˆ ë„ˆë¬´ ì§œì¦ì´ ë‚˜ê³  ì—´ì´ í™• ë°›ì•„ì„œ. ë­ë¼ í•œë§ˆë”” í–ˆë”ë‹ˆ ê·¸ë¦¬ ëì§€ ë­.\\në‚˜: ì•„ì´ê³  ê·¸ëŸ¬ì…¨êµ°ìš”. í™”ë‚  ë§Œ í•˜ë„¤ìš”. ê·¸ë¶„ê³¼ëŠ” í™”í•´í•˜ì…¨ë‚˜ìš”?\\nì‚¬ìš©ì: ë­˜ í™”í•´í•´ ê·¸ëƒ¥ ì‹œê°„ì´ ì§€ë‚˜ë©´ ê´œì°®ì•„ì§€ê² ì§€.\\n###\n" +
                "ë‚˜: ì˜¤ëŠ˜ ê¸°ë¶„ ì¢‹ì€ ì¼ì´ ìˆì—ˆë‚˜ìš”?\\n\n" +
                "ì‚¬ìš©ì:",
            conversation: "",
            isFinish: false,
            module: ""
        });
        sessionStatus.current = true
        setLoading(true)
    }

    //ì‚¬ìš©ì-sessionIDì˜ docì„ ê³„ì† ê´€ì°°í•˜ê³  ìˆë‹¤ê°€ ì—…ë°ì´íŠ¸ê°€ ë°œìƒí•˜ë©´ promptë¥¼ ì—…ë°ì´íŠ¸ í•˜ëŠ” useEffect í•¨ìˆ˜
    useEffect(() => {
        if (sessionStatus) {
            if (diaryNumber.current != "") {

                const unsuscribe = onSnapshot(doc(db, "session", props.userName, "diary", String(diaryNumber.current)), doc => {
                    console.log(doc.data()["outputFromLM"]);
                    receivedText.current = doc.data()["outputFromLM"]
                    const response = receivedText.current;
                    getLastSentence(response)
                })
                return () => unsuscribe();
            }
        }
    })

    /*async function getLastSentence(response) {
        let output = ""
        // console.log(response)
        let loc_1 = await response.lastIndexOf("ë‚˜: ");
        let temp_text = await response.slice(loc_1 + 3);
        // console.log(temp_text)
        if (temp_text.includes("\\n")) {
            let loc_2 = await temp_text.indexOf("\\n");
            output = await temp_text.substring(0, loc_2)
        } else {
