import {useEffect, useState, React} from "react";
import {addDoc, doc, setDoc, collection, serverTimestamp, onSnapshot, query, where, orderBy} from 'firebase/firestore'
import {auth, db} from "../firebase-config";

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from "react-bootstrap/Card";
import {ScaleLoader, BeatLoader} from "react-spinners";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {ko} from 'date-fns/esm/locale';

// const SDatePicker = styled(DatePicker)`
//   margin-top: 1.5rem;
//   width: 300px;
//   height: 42px;
//   box-sizing: border-box;
//   padding: 8px 20px;
//   border-radius: 4px;
//   border: 1px solid;
//   font-size: 12px;
// `;


function Writing(props) {

    //first, we need to set the date of the journal and create the doc for that specific date
    let [newDiary, setNewDiary] = useState("")
    let [sessionStatus, setSessionStatus] = useState(false)
    let [date, setDate] = useState(new Date());
    let [showConversation, setShowConversation] = useState(false)
    let [initiation, setInitiation] = useState(true)
    let [refresh, setRefresh] = useState(1)
    let [inputUser, setInputUser] = useState('')
    let [inConversation, setInConversation] = useState('')

    const conversationsRef = collection(db, "conversations")


    const createNewDoc = async (e) => {
        e.preventDefault();
        await addDoc(conversationsRef, {
            contents: [{
                message: '',
                role: ''
            }],
            createdAt: date,
            lastMessage: "",
            isFinish: false
        });
        setSessionStatus(true)
    }

    function setOpenConversation() {
        if (showConversation) {
            setShowConversation(false)
            console.log('나 여깄어~')
        } else {
            setShowConversation(true)
        }
    }

    function addConversationFromUser(input) {
        let temp = inConversation + 'B: ' + input + '\n'
        setInConversation(temp)
        setRefresh(refresh + 1)
    }

    if (sessionStatus == false) {
        return (
            <Container>
                <Row>
                    <div className="loading_box">
                        작성할 다이어리의 날짜를 선택해주세요
                        <DatePicker
                            selected={date}
                            onChange={date => setDate(date)}
                            locale={ko}
                            dateFormat="yyyy년 MM월 dd일"
                        />
                    </div>
                </Row>
                <Row>
                    <Col>
                        <div className="d-grid gap-2">
                            <Button
                                variant="primary"
                                style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                onClick={createNewDoc}
                            >
                                오늘의 다이어리 작성하기
                            </Button>
                        </div>
                    </Col>
                    <Col>

                    </Col>
                </Row>
            </Container>

        )
    } else {
        return (
            <Container>
                <Row>

                    <div>
                        {initiation === true ? <Initiation refresh={refresh}/> :
                            <Userinput prompt={props.prompt} setInputUser={setInputUser} inputUser={inputUser}
                                       addConversationFromUser={addConversationFromUser}/>}
                    </div>


                    <div className="inwriting_review_box">
                        <Container>
                            <Row xs={'auto'} md={1} className="g-4">
                                <Col>
                                    <Card style={{
                                        width: '100%',
                                    }}>
                                        <Card.Body>
                                            <Card.Title> <BeatLoader color="#007AFF" size={10}/> 일기 작성중</Card.Title>
                                            <Card.Subtitle className="mb-2 text-muted">
                                                <div>핵심키워드 도출 중</div>
                                            </Card.Subtitle>
                                            <Card.Text>
                                                오늘은 이기적인 사람들에 대해 생각해보았다. 나는 다른 사람을 배려하지만, 그들은 나를 그렇게 배려해주지는 않는 것 같다.
                                                하지만, 나의
                                                배려속에 상대방이 보답해주기를 바라는 마음이 있는 것은 아닐까? 이것은 '미숙한 착함'이 될 수 있다. 어떻게 하면 더 성숙할 수
                                                있을지
                                                고민해봐야겠다.
                                            </Card.Text>
                                        </Card.Body>
                                    </Card>
                                    <div
                                        style={{
                                            margin: "1em auto 2em"
                                        }}>
                                        <Button
                                            variant="secondary"
                                            size="sm"
                                            style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                            onClick={() => {
                                                setOpenConversation()
                                            }}>
                                            {showConversation === true ? <div>▲ 전체 대화내용 닫기</div> :
                                                <div>▼ 전체 대화내용 보기</div>}
                                        </Button>
                                    </div>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                </Row>
                <Row>
                    <Col>
                    </Col>
                    <Col>
                    </Col>
                </Row>
            </Container>
        )
    }


}

