import {useEffect, useState, useRef, React} from "react";
import {
    doc,
    getDoc,
    setDoc,
    collection,
    onSnapshot,
    getCountFromServer,
} from 'firebase/firestore'
import {db} from "../firebase-config";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from "react-bootstrap/Card";
import {ScaleLoader, BeatLoader} from "react-spinners";
import "react-datepicker/dist/react-datepicker.css";
import {useNavigate} from "react-router-dom";
import Modal from 'react-bootstrap/Modal';


function Writing(props) {

    let [loading, setLoading] = useState(false)

    const sessionStatus = useRef(false)
    const diaryNumber = useRef("");
    const receivedText = useRef(null);
    const receivedText2 = useRef(null);
    const receivedDiary = useRef("");
    const turnCount = useRef(0);
    const topic = useRef("");

    let [inputUser, setInputUser] = useState('')
    let [prompt, setPrompt] = useState(null)
    let [diary, setDiary] = useState("")
    let [diaryShow, setDiaryShow] = useState(false)
    const [modalShow, setModalShow] = useState(false)

    const sessionInputRef = useRef(null)
    const userNameRef = useRef(null)
    const userInput = useRef(null)
    const update = useRef(true)

    const [session, setSession] = useState("")
    const [userName, setUserName] = useState("")


    const navigate = useNavigate()
    const current = new Date();
    const date = `${current.getFullYear()}년 ${current.getMonth() + 1}월 ${current.getDate()}일`;


    useEffect(() => {
        if (sessionStatus) {
            if (session !== "") {
                const unsuscribe = onSnapshot(doc(db, "session", userName, "diary", session), doc => {
                    receivedText.current = doc.data()["fiveOptionFromLLM"]
                    receivedText2.current = doc.data()["conversation"]
                    const response = receivedText.current;
                    const response2 = receivedText2.current;
                    userInput.current = response2[response2.length - 1]
                    setOptions(response)
                })
                return () => {
                    unsuscribe();
                }
            }
        }
    })

    async function setOptions(response) {
        let a = setTimeout(() => {
            setPrompt(response)
            if (prompt.length === 0) {
                setLoading(true)
            } else {
                setLoading(false)
                console.log(prompt)
            }
        }, 10)
        return () => {
            clearTimeout(a)
        }
    }


    async function createNewDoc() {
        const coll = collection(db, "session", userName, "diary")
        const existingSession = await getCountFromServer(coll)
        diaryNumber.current = String(session)
        await setDoc(doc(db, "session", userName, "diary", session), {
            outputFromLM: "만나서 반가워요. 오늘 하루는 어떤가요?",
            fiveOptionFromLLM: [],
