import {useEffect, useState, useRef, React} from "react";
import {
    addDoc,
    doc,
    getDoc,
    setDoc,
    collection,
    serverTimestamp,
    onSnapshot,
    query,
    where,
    orderBy,
    getCountFromServer,
} from 'firebase/firestore'
import {auth, db} from "../firebase-config";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from "react-bootstrap/Card";
import {ScaleLoader, BeatLoader} from "react-spinners";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import {ko} from 'date-fns/esm/locale';

function Writing(props) {

    //ÌôîÎ©¥Í≥º Í∏∞Îä• Ï†ÑÌôò Ï†ïÏùòÎ•º ÏúÑÌïú state
    // let [sessionStatus, setSessionStatus] = useState(false) //ÏÑ∏ÏÖò ÏãúÏûë ÌôïÏù∏
    let [loading, setLoading] = useState(false) //Î°úÎî©(request API) ÌôïÏù∏
    const [tempText, setTemp] = useState('') //get-last-sentence ÌôïÏù∏
    let [refresh, setRefresh] = useState(0)

    //ÌòÑÏû¨ ÏÑ∏ÏÖò Ï†ïÎ≥¥
    let [currentSession, setCurrentSession] = useState("")//ÏÑ∏ÏÖò ÎÑòÎ≤Ñ
    const sessionStatus = useRef(false)
    const diaryNumber = useRef("");
    const receivedText = useRef("");
    let [inConversation, setInConversation] = useState("")//Î¥á,ÏÇ¨Ïö©Ïûê ÎåÄÌôîÍ∏∞Î°ù
    let [inputUser, setInputUser] = useState('')//ÏÇ¨Ïö©ÏûêÍ∞Ä textinputÏóê ÏûÖÎ†•Ìïú ÎÇ¥Ïö©
    let [promptForLLM, setPromptForLLM] = useState('') //LLMÏöîÏ≤≠ÏùÑ ÏúÑÌïú promptset
    let [prompt, setPrompt] = useState('')

    //Í∏∞ÌÉÄ
    let [showConversation, setShowConversation] = useState(false) //ÎåÄÌôî ÏõêÎ≥∏ Î≥¥Ïó¨Ï£ºÍ∏∞/Í∞ÄÎ¶¨Í∏∞

    //whisper Í¥ÄÎ†®

    async function createNewDoc() {
        const coll = collection(db, "session", props.userName, "diary")
        const existingSession = await getCountFromServer(coll)
        const sessionNum = await (existingSession.data().count + 1)
        diaryNumber.current = String(sessionNum)
        await setDoc(doc(db, "session", props.userName, "diary", String(sessionNum)), {
            outputFromLM: "",
            conversation: "",
            isFinish: false,
            module: ""
        });
        sessionStatus.current = true
        setLoading(true)
    }

    //ÏÇ¨Ïö©Ïûê-sessionIDÏùò docÏùÑ Í≥ÑÏÜç Í¥ÄÏ∞∞ÌïòÍ≥† ÏûàÎã§Í∞Ä ÏóÖÎç∞Ïù¥Ìä∏Í∞Ä Î∞úÏÉùÌïòÎ©¥ promptÎ•º ÏóÖÎç∞Ïù¥Ìä∏ ÌïòÎäî useEffect Ìï®Ïàò
    useEffect(() => {
        if (sessionStatus) {
            if (diaryNumber.current != "") {

                    const unsuscribe = onSnapshot(doc(db, "session", props.userName, "diary", String(diaryNumber.current)), doc => {
