import logo from './logo.svg';
import './App.css';
import {useState} from "react";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Stack from 'react-bootstrap/Stack';
import {Routes, Route, Link, useNavigate, Outlet} from 'react-router-dom'
import Calendar from "./Calendar";

// 구글파이어베이스 관련
import Auth from './pages/Auth'
import Cookies from 'universal-cookie'
import {signOut} from 'firebase/auth'
import {auth} from './firebase-config'

import Writing from "./pages/Writing"
import Loading from "./pages/Loading"
import DiaryList from "./pages/DiaryList"
import Home from "./pages/Home"

const cookies = new Cookies()


function App() {
    let navigate = useNavigate()
    //prompt
    let [prompt, setPrompt] = useState('기본 입력')
    //global_user-system conversation
    let [conversation, setConversation] = useState('')

    //firebase setting
    const [isAuth, setIsAuth] = useState(cookies.get("auth-token"))
    const [room, setRoom] = useState(null)
    const roomInputRef = useRef(null)

    const signUserOut = async () => {
        await signOut(auth)
        cookies.remove("auth-token")
        setIsAuth(false)
        setRoom(null)
    }


    return (
        <div className="App">
            <div>
                <div>
                    <Navbar bg="light" variant="light">
                        <Container>
                            <Navbar.Brand onClick={() => {
                                navigate('/')
                            }}>
                                <Stack gap={0}>
                                    <div className="nav_title_black">마음챙김</div>
                                    <div className="nav_title_blue">다이어리</div>
                                </Stack>
                            </Navbar.Brand>
                            <Nav className="me-auto">
                                <Nav.Link onClick={() => {
                                    navigate('/writing')
                                }}>일기 작성하기</Nav.Link>
                                <Nav.Link onClick={() => {
                                    navigate('/list')
                                }}>일기 돌아보기</Nav.Link>
                            </Nav>
                            <Nav>
                                <Nav.Link>Today<Calendar/></Nav.Link>
                            </Nav>
                        </Container>
                    </Navbar>
                </div>
                <Routes>
                    <Route path="/" element={
                        <div>
                            {isAuth ? (<Auth setIsAuth={setIsAuth}/>) : (<div>

                            </div>)}
                        </div>
                    }/>
                    <Route path="/writing" element={<div><Writing prompt={prompt} setPrompt={setPrompt}/></div>}/>
                    <Route path="/list" element={<div><DiaryList/></div>}/>
                    <Route path="/loading" element={<div><Loading/></div>}/>
                    <Route path="*" element={<div>404~ 없는페이지임</div>}/>
                </Routes>
            </div>
        </div>
    );
}

export default App;
