    async function getLastSentence(response) {
        // let addconversation = inConversation + 'A: ' + output + '\n'
        // setInConversation(addconversation)
        let a = setTimeout(() => {
            setPrompt(response)
            console.log(prompt)
            if ((prompt).trim() === "") {
                setLoading(true)
            } else {
                setLoading(false)
            }
            // setLoading(false)
        }, 10)
        return () => {
            clearTimeout(a)
        }
    }

    async function assemblePrompt() {
        const readyRequest = []
        const docRef3 = doc(db, "session", props.userName, "diary", String(diaryNumber.current));
        const docSnap = await getDoc(docRef3);
        if (docSnap.exists()) {
            const readyRequest = docSnap.data().conversation;
            console.log(readyRequest)
            requestPrompt(readyRequest, props.userName, diaryNumber.current)
        } else {
            // doc.data() will be undefined in this case
            console.log("No such document!");
        }
    }

    function requestPrompt(text, user, num) {
        // console.log(text)
        return fetch('http://localhost:8000', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
                'user': user,
                'num': num
            })
        })
            .catch(err => console.log(err));
    }


    function requestSummerization() {
        setLoading(true)
        return fetch('http://localhost:8000/diary', {
            method: 'POST',
            body: JSON.stringify({
                'user': props.userName,
                'num': diaryNumber.current
            })
        })
            .catch(err => console.log(err));
    }

    function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array
    }

    /*    function shuffleArray(array) {
            return array
        }*/


    /*async function addConversationFromUser(input) {
        let temp = "ÎÇò: " + prompt + "\\n" + 'ÏÇ¨Ïö©Ïûê: ' + input + "\\n";
        const docRef2 = doc(db, "session", props.userName, "diary", String(diaryNumber.current));
        const docSnap2 = await getDoc(docRef2);
