    async function addConversationFromUser(input) {
        let system_temp = {"role": "assistant", "content": prompt}
        let user_temp = {"role": "user", "content": input};

        const docRef2 = doc(db, "session", props.userName, "diary", String(diaryNumber.current));
        const docSnap2 = await getDoc(docRef2);
        if (docSnap2.exists()) {
            const message = docSnap2.data().conversation;
            message[message.length] = system_temp;
            message[message.length] = user_temp;
            let a = setTimeout(async () => {
                await setDoc(docRef2, {
                    conversation: message,
                    outputFromLM: ""
                }, {merge: true});
                assemblePrompt();
                setLoading(true);
            }, 500)
            return () => {
                clearTimeout(a)
            }
        } else {
            console.log("Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå");
        }
    }


    if (sessionStatus.current === false) {
        return (
            <Container>
                <Row>
                    <div className="loading_box">
                        Ïò§ÎäòÏùò ÎßàÏùåÏ±ôÍπÄ ÏÑ∏ÏÖòÏùÑ ÏãúÏûëÌï©ÎãàÎã§.
                    </div>
                </Row>
                <Row>
                    <Col>
                        <div className="d-grid gap-2">
                            <Button
                                variant="primary"
                                style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                onClick={createNewDoc}
                            >Ïò§ÎäòÏùò Îã§Ïù¥Ïñ¥Î¶¨ ÏûëÏÑ±ÌïòÍ∏∞
                            </Button>
                        </div>
                    </Col>
                    <Col>
                    </Col>
                </Row>
            </Container>

        )
    } else {
        return (
            <Container>
                <Row>
                    <div>
                        {loading === true ? <Loading/> :
                            <Userinput prompt={prompt} setInputUser={setInputUser} inputUser={inputUser}
                                       addConversationFromUser={addConversationFromUser} requestSummerization={requestSummerization} setLoading={setLoading}/>}
                    </div>
                </Row>
                <Row>
                    {diary === "" ? <div></div>: <DiaryView diary={diary}/>}
                </Row>
            </Container>
        )
    }


}

function Loading() {
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="loading_box">
                            <div>
                                <ScaleLoader
