import {useEffect, useState} from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import React from 'react';
import {ScaleLoader} from "react-spinners";


function Writing(props) {


    //처음인지 아닌지 확인하는 변수
    let [initiation, setInitiation] = useState(true)
    let [refresh, setRefresh] = useState(1)
    let [update, setUpdate] = useState(1)

    //로컬 대화내용
    let [inConversation, setInConversation] = useState('')
    //대화 입력 업데이트
    let [inputUser, setInputUser] = useState('')

    var ws = null;
    useEffect(() => {
        ws = new WebSocket("ws://localhost:8000/ws");
        ws.onopen = () => ws.send(inConversation);
        // onmeaage();
    });
    const onmeaage = () => {
        ws.onmessage = (event) => {
            console.log(event);
        };
    };
    const webgazer = window.webgazer;


    useEffect(() => {
        setInitiation(true)
        predictNextSentence(inConversation)
    }, [refresh]);

    // useEffect(() => {
    //     let temp = inConversation + 'B: ' + inputUser + '\n'
    //     setInConversation(temp)
    //     setRefresh(refresh + 1)
    // }, [inputUser]);


    function addConversationFromUser(input) {
        let temp = inConversation + 'B: ' + input + '\n'
        setInConversation(temp)
        function addInput(inConversation) {
            // ws = new WebSocket("ws://localhost:8000/ws");
            ws.onopen = () => ws.send(inConversation);
            setRefresh(refresh + 1)
        }

        // // setUpdate(update + 1)
    }

    //llm요청 함수
    function predictNextSentence(text, items) {
        return fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
            })
        })
            .then((response) => response.json())
            .then((data) => {
                let prompt = getLastSentence(data)
                //prompt만 추출해서 저장해야함
                props.setPrompt(prompt)
                let a = setTimeout(() => {
                    setInitiation(false)
                    //console.log(inConversation)
                }, 1000);

            });
    }

    function getLastSentence(response) {
        let loc_1 = response.lastIndexOf("A: ");
        let temp_text = response.slice(loc_1 + 3);
        let loc_2 = temp_text.indexOf("\n");
        let output = temp_text.substring(0, loc_2)
        let addconversation = inConversation + 'A: ' + output + '\n'
        setInConversation(addconversation)
        return output
    }


    return (
        <div>
            <div>{initiation === true ? <Initiation refresh={refresh}/> :
                <Userinput prompt={props.prompt} setInputUser={setInputUser} inputUser={inputUser}
                           addConversationFromUser={addConversationFromUser}/>}</div>
            {/*<div>{inConversation}</div>*/}
        </div>
    )
}

function Initiation(props) {
    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="loading_box">
                            <div>
                                <ScaleLoader
                                    color="#007AFF"
                                    speedMultiplier={0.9}
                                />
                            </div>
                            <div>{props.refresh === 1 ? <div>곧 일기 작성이 시작됩니다</div> : <div>지금까지의 이야기를 정리중입니다</div>}</div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">✏️ 나의 일기 입력하기</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            disabled
                            readOnly
                        />
                        <Form.Text id="userInput" muted>
                            📝 정해진 양식이나 응답이 없어요. 편안하고 자유롭게 최근에 있었던 일을 작성해주세요 .
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        disabled={true}
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                    >응답 기록하기</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

function Userinput(props) {

    let [temp_input, setTemp_input] = useState('')


    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="prompt_box">
                            <div className="tte">
                                {props.prompt}
                            </div>
                        </div>
                    </Col>
                </Row>
                <Row>
                    <div className="writing_box">
                        <Form.Label htmlFor="userInput">✏️ 나의 일기 입력하기</Form.Label>
                        <Form.Control
                            type="input"
                            as="textarea"
                            rows={3}
                            id="userInput"
                            onChange={(e) => {
                                //props.setInputMsg(e.target.value)
                                //console.log(e.target.value)
                                setTemp_input(e.target.value)
                            }}
                        />
                        <Form.Text id="userInput" muted>
                            📝 정해진 양식이나 응답이 없어요. 편안하고 자유롭게 최근에 있었던 일을 작성해주세요 .
                        </Form.Text>
                    </div>
                    <Container>
                        <Row>
                            <Col>
                                <div className="d-grid gap-2">
                                    <Button
                                        variant="primary"
                                        style={{backgroundColor: "007AFF", fontWeight: "600"}}
                                        onClick={() => {
                                            props.addConversationFromUser(temp_input)
                                        }}>응답 기록하기</Button>
                                </div>
                            </Col>
                            <Col>
                            </Col>
                        </Row>
                    </Container>
                </Row>
            </Container>
        </div>
    )
}

export default Writing
