import logo from './logo.svg';
import './App.css';
import {useState} from "react";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';


function App() {

    let [inputMsg, setInputMsg] = useState('')
    let [prompt, setPrompt] = useState('아직 시작전')

    function predictNextSentence(text, items) {
        return fetch('http://0.0.0.0:8000/', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
            })
        })
            .then((response) => response.json())
            .then((data) => setPrompt(data));
    }

    function userInput(text, items) {
        return fetch('http://0.0.0.0:8000/user', {
            method: 'POST',
            body: JSON.stringify({
                'text': text,
            })
        })
            .then((response) => response.json())
            .then((data) => setPrompt(data));
    }


    return (
        <div className="App">
            <div>
                <div>
                    <Navbar bg="light" variant="light">
                        <Container>
                            <Navbar.Brand href="#home">마음챙김 다이어리</Navbar.Brand>
                            <Nav className="me-auto">
                                <Nav.Link href="#home">일기 작성하기</Nav.Link>
                                <Nav.Link href="#features">일기 돌아보기</Nav.Link>
                            </Nav>
                        </Container>
                    </Navbar>
                </div>
                <h4>{prompt}</h4>
                <input onChange={(e) => {
                    setInputMsg(e.target.value)
                }}></input>
                <button onClick={() => {
                    userInput({inputMsg}, 0.5)
                }}>내 응답 저장
                </button>
                <button onClick={() => {
                    predictNextSentence({inputMsg}, 0.5)
                }}>api 호출
                </button>
            </div>
        </div>
    );
}

export default App;
